type ParseFun a = [Token] -> Err a

myLLexer = myLexer

type Verbosity = Int

putStrV :: Verbosity -> String -> IO ()
putStrV v s = when (v > 1) $ putStrLn s

-- runFile :: (Print a, Show a) => Verbosity -> ParseFun a -> FilePath -> IO ()
-- runFile v p f = putStrLn f >> readFile f >>= run v p

run :: Bool -> 
       Verbosity -> 
       ParseFun (Program (Maybe (Int, Int))) -> 
       String -> 
       IO ()
run showQuads v p s = let ts = myLLexer s in case p ts of
    Bad s -> putStrLn s >> exitFailure
    Ok  tree ->
        let typeChecked = typeCheck tree in
        printTypeCheckResult showQuads typeChecked >>
        case typeChecked of
            (BadChecked _) -> exitFailure
            (GoodChecked _) -> exitSuccess

showTree :: (Show a, Print a) => Int -> a -> IO ()
showTree v tree
 = do
      putStrV v $ "\n[Abstract Syntax]\n\n" ++ show tree
      putStrV v $ "\n[Linearized tree]\n\n" ++ printTree tree

usage :: IO ()
usage = do
    hPutStrLn stderr $ unlines
        [ "usage: Call with one of the following argument combinations:"
        , "  --help          Display this help message."
        , "  (no arguments)  Parse stdin verbosely."
        , "  (files)         Parse content of files verbosely."
        , "  -s (files)      Silent mode. Parse content of files silently."
        ]
    exitFailure

printTypeCheckResult :: Bool -> TypeCheckResult -> IO ()
printTypeCheckResult showQuads (GoodChecked pAst) = do
    hPutStrLn stderr "OK"
    let compiled = compile showQuads pAst
    putStr compiled
printTypeCheckResult _ (BadChecked (m, err)) = setSGR [Reset] << case m of
        Nothing -> do
            setSGR [SetColor Foreground Vivid Red]
            hPutStrLn stderr "ERROR: "
            hPutStrLn stderr $ show err
        Just (line, col) -> do
            setSGR [SetColor Foreground Vivid Red]
            setSGR [SetConsoleIntensity BoldIntensity]
            hPutStrLn stderr $ "ERROR " ++ show line ++ ":" ++ show col ++ ": "
            setSGR [SetDefaultColor Foreground]
            setSGR [SetConsoleIntensity NormalIntensity]
            hPutStrLn stderr $ show err
    where (<<) = flip (>>)

main :: IO ()
main = do
    args <- getArgs
    case args of
        ["--help"] -> usage
        ["-q"] -> getContents >>= run True 2 pProgram
        [] -> getContents >>= run False 2 pProgram
